import React from "react";
import data from "../data";
const Main = () => {
  let imgMeme = "";
  function genImgUrl() {
    const memeImg = data.map((item) => {
      return item.coverImg;
    });
    const memeImgIndex = Math.floor(Math.random() * memeImg.length - 1) + 1;
    const [memeImage, setMemeImage] = React.useState(memeImage[memeImgIndex]);
    // console.log(memeImage)
    imgMeme = memeImage;
    // console.log(memeImg[memeImgIndex], `is at index "${memeImgIndex}"`)
  }
  return (
    <main>
      <section className="meme-gen">
        <input type="text" placeholder="Top text" />
        <input type="text" placeholder="Bottom text" />
        <button onClick={genImgUrl}>Get a new meme image</button>
        <div className="meme-img">
          <img src="" alt="" />
        </div>
      </section>
    </main>
  );
};

export default Main;
