import data from "../data";
const Main = () => {
  const [memeImage, setMemeImage] = React.useState("");
  function genImgUrl() {
    const memeImg = data.map((item) => {
      return item.coverImg;
    });
    const memeImgIndex = Math.floor(Math.random() * 99) + 1;
    setMemeImage(memeImg[memeImgIndex]);
