import React from "react";
import data from "../data";
const Main = () => {
  const [memeImage, setMemeImage] = React.useState("");
  function genImgUrl() {
    const memeImg = data.map((item) => {
      return item.coverImg;
    });
    const memeImgIndex = Math.floor(Math.random() * 99) + 1;
    setMemeImage(memeImg[memeImgIndex]);
    console.log(memeImage)
  }
  return (
    <main>
      <section className="meme-gen">
        <div className="inputs">
        <input type="text" placeholder="Top text" />
        <input type="text" placeholder="Bottom text" />
        </div>

        <button onClick={genImgUrl}>Get a new meme image</button>
        <div className="meme-img">
          <img src={`../images${memeImage}`} alt="" />
        </div>
      </section>
    </main>
  );
};

export default Main;
