export default
function greeting(name){
    const d = new Date()
    // alert(d.getHours())
    if(d >= 4 || d <= 11){
    alert(`Good Morning ${name}`)
    let reply = prompt("what your goal for today")
    alert(reply)
    }
    else if(d >= 12 || d <= 16){
    alert(`Good Afternoon ${name} What have you done since Morning?`)
    let reply = prompt("your reply")
    confirm(reply)
}
    else if(d >= 17 || d <= 19)
    alert(`Good Evening ${name}`)
    else if(d >= 20 || d <= 3)
    alert(`Good Night ${name}`)
}
greeting('Leo')
