import ReactDom from "react-dom";
import data from "./data";
import greeting from "./day";
import day from './day'

const App = () => {

  const [result, setResult] = React.useState('Yes')
  const [thingArr, setThings] = React.useState(["thing 1", "thing 2"]);
  const arr = thingArr.map((item) => {
    return <p
    key={item}
    >
    {item}</p>;
  });

  const eventHandle = () =>{
    setResult("")
  }
  function actionPerform() {
    const arrTxt = `thing ${thingArr.length + 1}`;
    // thingArr.push(arrTxt);
    setThings(prevState => [...prevState, arrTxt])
    // console.log(thingArr)
    // const mapedData = data.map((item) => {
    //   return item.image;
    // });
    // const randNum = Math.floor(Math.random() * 23) + 1;
    // console.log(mapedData[randNum]);
    // console.log(randNum);
    // alert("Action Occur!!!!");
  }
  return (
    <>
      <h1>Welcome React App</h1>
      <button 
      onClick={actionPerform}  
      >Action!!</button>
      {arr}
      {/* {alert(greeting)} */}

      <button
      onClick={eventHandle}
      >
        Click Me!!
      </button>
      <h2>{result}</h2>
    </>
  );
};

ReactDom.render(<App />, document.getElementById("root"));
