import React from "react";

const Count = () => {
  const [count, setCount] = React.useState(0);
  function countDown() {
    if (count === 0) return;
    setCount(prevCount => prevCount );
  }
  function countUp() {
    setCount(count + 1);
  }
  return (
    <div>
      <button onClick={countDown}>&minus;</button>
      <h1>{count}</h1>
      <button onClick={countUp}>+</button>
    </div>
  );
};

export default Count;
