import React, {useState,useEffect} from 'react'

const Effect = ()=>{
    const [windowWidth, setWindowWidth] = useState(window.innerWidth)
    useEffect(() =>{
        window.addEventListener('resize')
    })

    return <h1>Window width{window.innerWidth}</h1>;
}
export default Effect
