import React from "react";
import ReactDom from "react-dom";
import data from "./data";
import greeting from "./day";
// import day from "./day";
import "./style.css";

const App = () => {
  const [count, setCount] = React.useState(0);

  const countDown = () => {
    if (count === 0) return;
    setCount((prevCount) => prevCount - 1);
  };
  const countUp = () => {
    setCount((prevCount) => prevCount + 1);
  };
const [memesImg, setMemesImg] = React.useState("");
  const generateImg = () => {
    const mapData = data.map((item) => {
      return item.image;
    });
    const arrImg = Math.floor(Math.random() * 23) + 1;
    setMemesImg(mapData[arrImg]);
  };
  return (
    <>
      <button className="left" onClick={countDown}>
        {" "}
        &minus;
      </button>
      <h1>{count}</h1>
      <button className="right" onClick={countUp}>
        {" "}
        +{" "}
      </button>

      <button className="img-generator" onClick={generateImg}>
        generate image
      </button>

      <div className="img-container">
        <img src={require(`./images${memesImg}`)} alt="" className="img-gen" />
      </div>
    </>
  );
};

ReactDom.render(<App />, document.getElementById("root"));
